var searchController = function($scope, $http, $location){
	$scope.synonyms = SYNONYMS;
	$scope.synonymdetails = SYNONYMS_DETAILS;
	$scope.query = 2;
	$scope.pageSize = 10;
	$scope.currentPage = 0;
	$scope.numberOfPages = function(synonymsCount){
		return Math.ceil( (synonymsCount || $scope.synonyms.length) / $scope.pageSize );
	}
	$scope.currentPageForDetails = 0;
	$scope.numberOfPagesForDetails = function(synonymDetailsCount){
		return Math.ceil( (synonymDetailsCount || $scope.synonymdetails.length) / $scope.pageSize );
	}
	
	$scope.tableRowClicked = function(synonym){ //alert(synonym); console.log(synonym);
		$location.path(synonym.id); // path not hash
	}


	$scope.verseno = 100;
	$scope.getVerse = function(verseno){ if(!parseInt($scope.query) ) return;
		var verse = {
			no: verseno,
			words: 				gq.dsbuck[verseno],
			words_nostem: 		gq.dslem[verseno],
			words_novowel:  	BuckToBare(gq.dsbuck[verseno]),
			words_nostem_novowel: BuckToBare(gq.dslem[verseno]),
			words_root: 		gq.dsroot[verseno],
			word_en: 			gq.dsword[verseno],
			words_corpus: 		gq.dscorpus[verseno],		
			words_translit: 	gq.dstranslit[verseno],
			words_trans: 		gq.dstrans[verseno] 
		}
		$scope.verse = verse; console.log(verse); //debugger;
		//console.log( JSON.stringify( getVerse(verseno) ) );
		return verse;
	}
	
	$scope.regexMatch = _.memoize(function(regex, target){ //debugger;
									return target.match( regex );
		});
	
	$scope._searchPhonetic = function(){ //debugger;
		var thisSearch, result = '', _target, startTime = new Date().getTime(), endTime, lastSearchActual, thisSearchActual, lastSearchParts, isLastSearchContinue, isLastSearchAdd, isLastSearchRemove, doBreak,regex, tries=0, searchActual='', wildcard = ".{0,2}?", wildcard2 = ".*?", wildcardCh = '~', lastSearchHasWildcard, lastSearch, lastResult;
		if(!_phon){ _phon = $( gq.dstranslit.join('\n') ).text(); } _target = _phon;
		thisSearch = $scope.search;
		thisSearchActual = thisSearch.replace( RegExp(wildcard, "g"), '' );
		if(!thisSearch || thisSearch.length < 3){ $scope.search_regex = ''; $scope.result = 'Enter a search word with atleast 3 characters. returning all..\n\n\n' + _phon.substring(0, 10000); return; }
		while(!doBreak && tries <= 2){
			if($scope.lastSearch){
				lastSearch = $scope.lastSearch;
				lastResult = $scope.lastResult;
				lastSearchActual = lastSearch.replace( RegExp(escapeRegex( wildcard ), "g"), '' );
				lastSearchHasWildcard = (lastSearch.indexOf( wildcard ) != -1);
				lastSearchParts = thisSearchActual.split( lastSearchActual );
				isLastSearchAdd = (_.first(lastSearchParts) == "") && (lastSearchParts.length == 2);//this is addition to last search
				if(!isLastSearchAdd && (thisSearchActual.length < lastSearchActual.length)){
					lastSearchParts = lastSearchActual.split( thisSearchActual );
					isLastSearchRemove = (lastSearchParts.length ==2 && lastSearchParts[1].length >= 1);
				}
				if(isLastSearchAdd && lastResult && lastResult.length > 0){ 
					_target = lastResult;  
				}
			}else{ _target = _phon; /* i.e. search entire data */ }
			
			if(isLastSearchRemove /*&& lastSearchHasWildcard*/){ debugger; //strip out as many characters from the regex as were removed, taking wildcard as 1 char!
				var lastSearchTemp = lastSearch.replace( RegExp(escapeRegex( wildcard ), "g"), wildcardCh );
				var charsToRemove = lastSearchParts[1].length;
				if( lastSearchTemp[ lastSearchTemp.length - charsToRemove - 1 ] == wildcardCh ) ++charsToRemove; //ensure not ending in wildcard!
				lastSearchTemp = lastSearchTemp.substring(0, lastSearchTemp.length - charsToRemove);
				thisSearch = lastSearchTemp.replace( RegExp(escapeRegex( wildcardCh ), "g"), wildcard );
			}
			else if(isLastSearchAdd){//debugger;
				if(!lastSearchHasWildcard && tries<1){
					thisSearch = lastSearch + lastSearchParts[1];
				}else if( lastSearchHasWildcard && tries < 1){//if wild char not there, add it. else dont add again - unless after 1st try.				
					thisSearch = lastSearch + lastSearchParts[1];
				}else if( lastSearchHasWildcard && tries >= 2){
					thisSearch = lastSearch + wildcard2 + lastSearchParts[1];
				}else if( lastSearchHasWildcard && tries >= 1){
					thisSearch = lastSearch + wildcard + lastSearchParts[1];
				}else{//either wild char not there.. so add. or its 2nd try and 1st try failed without wildchar. so add.
					thisSearch = lastSearch + wildcard + lastSearchParts[1];
				}
			}//else{ debugger; }
			regex = RegExp('.*' + thisSearch + '.*$', "gmi");
			console.log( ++tries + ': \t'+ thisSearchActual +' -- '+ thisSearch +'\t'+ lastSearch +'\t\t'+ regex);
			result = $scope.regexMatch( regex, _target ); endTime = new Date().getTime(); 
		
			_history.push( {k: thisSearch, n: (result || []).length, b: _target.length, //n # of results. b # of bytes searched
							t: (endTime - startTime), l: (endTime - ($scope.lastTime || endTime)) //t time taken; l time since last search
			}); console.log( JSON.stringify(_history) );
		
			if(result && result.length > 0){ 
				$scope.lastSearch = thisSearch; 
				$scope.lastTime = endTime;
				$scope.search_regex = thisSearch;
				$scope.lastResult = result; doBreak = true;
			}else{ continue; }
		}
		if(result && result.length > 0){ $scope.lastResult = result.join('\n'); $scope.result = result.length + ' results.\n\n' + $scope.lastResult; return; }
		else{ $scope.result = 'no results. returning all..\n\n\n' + _phon.substring(0, 10000); return; }
	}
	
	
	
	$scope._SEARCH_DEBOUNCE_DELAY = 150;
	$scope.search = $scope.lastSearch = "";
	$scope.lastTime = new Date().getTime();
	$scope.lastResult = [];
	$scope.searchPhonetic = _.debounce( $scope._searchPhonetic, 
										$scope._SEARCH_DEBOUNCE_DELAY
										,true
									  );
	$scope.EnToAr = EnToAr;
	$('#searchPhonetic').focus();
	$scope.searchPhonetic();
}
